script:
fetch('data/posts.json')
  .then(res => res.json())
  .then(posts => {
    let rssItems = posts.map(p => `


        ${p.title}
        https://xplorestartup.com/${p.url}
        ${new Date(p.date).toUTCString()}
        ${p.excerpt}

    `).join('');

    const rssFeed = `



          Xplore Startup News
          https://xplorestartup.com/news.html
          Latest startup news and insights
          ${rssItems}


    `;

    // For demonstration: logs XML; in production save as rss.xml on server
    console.log(rssFeed);
});
